const clearIcon = document.querySelector(".clear-icon");
const searchBar = document.querySelector(".search");
const searchQuery = document.querySelector(".searchQuery");
const setSearchQuery = document.querySelector(".setSearchQuery");

const SearchBar = ({ searchQuery, setSearchQuery }) =>
  searchBar.addEventListener("keyup", () => {
    if (searchBar.value && clearIcon.style.visibility != "visible") {
      clearIcon.style.visibility = "visible";
    } else if (!searchBar.value) {
      clearIcon.style.visibility = "hidden";
    }
  });

clearIcon.addEventListener("click", () => {
  searchBar.value = "";
  clearIcon.style.visibility = "hidden";
});

<form action="/" method="get">
  <label htmlFor="header-search">
    <span className="wrapper visually-hidden">
      Search blog posts
      <img
        class="search-icon"
        src="data:image/svg+xml;utf8;base64,"
      />
      <input class="search" placeholder="Search" type="text"></input>
    </span>
  </label>
  <input
    value={searchQuery}
    onInput={(e) => setSearchQuery(e.target.value)}
    type="text"
    id="header-search"
    placeholder="Search items to Barter"
    name="s"
  />
  <button type="submit">Search</button>
</form>;

export default SearchBar;
